int main()
{
	int hinverted = 0;
	int vinverted = 0;
	int toggle = 0;
	int hmoved = 0;
	int vmoved = 0;
	uint16_t x = 50;
	uint16_t y = 50;
	uint16_t oldx = x;
	uint16_t oldy = y;
	initClock();
	initSysTick();
	setupIO();
	initSerial();


	initBackground();
	// eputs("test0");
	// fillRectangle(127, 50, 2, 20, 504); // x, y, width, height, colour

	while(1)
	{
		hmoved = vmoved = 0;
		hinverted = vinverted = 0;
		if ((GPIOB->IDR & (1 << 4))==0) // right pressed
		{					
			if (x < 110)
			{
				x = x + 1;
				hmoved = 1;
				hinverted=0;
			}						
		}
		if ((GPIOB->IDR & (1 << 5))==0) // left pressed
		{			
			
			if (x > 10)
			{
				x = x - 1;
				hmoved = 1;
				hinverted=1;
			}			
		}
		if ( (GPIOA->IDR & (1 << 11)) == 0) // down pressed
		{
			if (y < 140)
			{
				y = y + 1;			
				vmoved = 1;
				vinverted = 0;
			}
		}
		if ( (GPIOA->IDR & (1 << 8)) == 0) // up pressed
		{			
			if (y > 16)
			{
				y = y - 1;
				vmoved = 1;
				vinverted = 1;
			}
		}
		if ((vmoved) || (hmoved))
		{
			fillRectangle2(oldx,oldy,BIRDWIDTH,BIRDHEIGHT,0);
			oldx = x;
			oldy = y;					
			if (hmoved)
			{
				if (toggle)
					putImage2(x,y,BIRDWIDTH,BIRDHEIGHT,bird1,hinverted,0);
				else
					putImage2(x,y,BIRDWIDTH,BIRDHEIGHT,bird1,hinverted,0);
				
				toggle = toggle ^ 1;
			}
			else
			{
				putImage2(x,y,BIRDWIDTH,BIRDHEIGHT,bird1,0,vinverted);
			}
		}		
		delay(50);
	}
	return 0;
}


