void putImage2(uint16_t x, uint16_t y, uint16_t width, uint16_t height, const uint16_t *Image, int hOrientation, int vOrientation)
{
    uint16_t Colour;
    uint32_t offset = 0;
    uint16_t baseX = x;
    uint16_t baseY = y;

    openAperture2(x, y, x + width - 1, y + height - 1); // open the dimensions of the image for writing
    DCHigh2();

    if (vOrientation == 0)
    {
        for (y = 0; y < height; y++)
        {
            offset = y * width;
            for (x = 0; x < width; x++)
            {
                Colour = Image[offset + x];
                uint16_t screenX = baseX + x;
                uint16_t screenY = baseY + y;

                if (Colour == 5536) {
                    if (screenY >= bStart && screenY < (bStart + bHeight)) {
                        int index = screenX % bWidth;
                        int imIndex = (screenY - bStart) * bWidth + index;
                        Colour = background[imIndex];
                    } else if (screenY < bStart) {
                        Colour = 2510;
                    } else {
                        Colour = 12562;
                    }
                }

                transferSPI162(Colour);
            }
        }
    }
    else
    {
        for (int k = 0; k < height; k++)
        {
            offset = (height - (k + 1)) * width;
            for (int j = 0; j < width; j++)
            {
                Colour = Image[offset + j];
                uint16_t screenX = baseX + j;
                uint16_t screenY = baseY + k;

                if (Colour == 5536) {
                    if (screenY >= bStart && screenY < (bStart + bHeight)) {
                        int index = screenX % bWidth;
                        int imIndex = (screenY - bStart) * bWidth + index;
                        Colour = background[imIndex];
                    } else if (screenY < bStart) {
                        Colour = 2510;
                    } else {
                        Colour = 12562;
                    }
                }

                transferSPI162(Colour);
            }
        }
    }
}

void fillRectangle2(uint16_t x,uint16_t y,uint16_t width, uint16_t height, uint16_t colour)
{
	uint16_t baseX = x;
	uint16_t baseY = y;
	openAperture2(x, y, x + width - 1, y + height - 1);
	DCHigh2();

	for (y = 0; y < height; y++){
		for (x = 0; x < width; x++){
			uint16_t screenX = baseX + x;
			uint16_t screenY = baseY + y;

			if (screenY >= bStart && screenY < (bStart + bHeight)) {
				int index = screenX % bWidth;
				int imIndex = (screenY - bStart) * bWidth + index;
				colour = background[imIndex];
			} 
			else if (screenY < bStart) {
				colour = 2510;
			} 
			else {
				colour = 12562;
			}

			transferSPI162(colour);
		}
	}
}



void initBackground(){
	putImage(0,bStart,bWidth,bHeight,background,0,0);
	putImage(32,bStart,bWidth,bHeight,background,0,0);
	putImage(64,bStart,bWidth,bHeight,background,0,0);
	putImage(96,bStart,bWidth,bHeight,background,0,0);
	fillRectangle(0, 0, 128, 65, 2510);
	fillRectangle(0, 139, 128, 21, 12562);
}

int anyButtonPressed(){
	if (downPressed() || upPressed() || leftPressed() || rightPressed()){
		return 1;
	}
	else{
		return 0;
	}
}

int downPressed(){
	if ( (GPIOA->IDR & (1 << 11)) == 0){
		return 1;
	}
	else{
		return 0;
	}
}

int upPressed(){
	if ( (GPIOA->IDR & (1 << 8)) == 0){
		return 1;
	}
	else{
		return 0;
	}
}

int rightPressed(){
	if ((GPIOB->IDR & (1 << 4))==0){
		return 1;
	}
	else{
		return 0;
	}
}

int leftPressed(){
	if ((GPIOB->IDR & (1 << 5))==0){
		return 1;
	}
	else{
		return 0;
	}
}

void initSysTick(void)
{
	SysTick->LOAD = 48000;
	SysTick->CTRL = 7;
	SysTick->VAL = 10;
	__asm(" cpsie i "); // enable interrupts
}
void SysTick_Handler(void)
{
	milliseconds++;
}
void initClock(void)
{
// This is potentially a dangerous function as it could
// result in a system with an invalid clock signal - result: a stuck system
        // Set the PLL up
        // First ensure PLL is disabled
        RCC->CR &= ~(1u<<24);
        while( (RCC->CR & (1 <<25))); // wait for PLL ready to be cleared
        
// Warning here: if system clock is greater than 24MHz then wait-state(s) need to be
// inserted into Flash memory interface
				
        FLASH->ACR |= (1 << 0);
        FLASH->ACR &=~((1u << 2) | (1u<<1));
        // Turn on FLASH prefetch buffer
        FLASH->ACR |= (1 << 4);
        // set PLL multiplier to 12 (yielding 48MHz)
        RCC->CFGR &= ~((1u<<21) | (1u<<20) | (1u<<19) | (1u<<18));
        RCC->CFGR |= ((1<<21) | (1<<19) ); 

        // Need to limit ADC clock to below 14MHz so will change ADC prescaler to 4
        RCC->CFGR |= (1<<14);

        // and turn the PLL back on again
        RCC->CR |= (1<<24);        
        // set PLL as system clock source 
        RCC->CFGR |= (1<<1);
}
void delay(volatile uint32_t dly)
{
	uint32_t end_time = dly + milliseconds;
	while(milliseconds != end_time)
		__asm(" wfi "); // sleep
}

void enablePullUp(GPIO_TypeDef *Port, uint32_t BitNumber)
{
	Port->PUPDR = Port->PUPDR &~(3u << BitNumber*2); // clear pull-up resistor bits
	Port->PUPDR = Port->PUPDR | (1u << BitNumber*2); // set pull-up bit
}
void pinMode(GPIO_TypeDef *Port, uint32_t BitNumber, uint32_t Mode)
{
	/*
	*/
	uint32_t mode_value = Port->MODER;
	Mode = Mode << (2 * BitNumber);
	mode_value = mode_value & ~(3u << (BitNumber * 2));
	mode_value = mode_value | Mode;
	Port->MODER = mode_value;
}
int isInside(uint16_t x1, uint16_t y1, uint16_t w, uint16_t h, uint16_t px, uint16_t py)
{
	// checks to see if point px,py is within the rectange defined by x,y,w,h
	uint16_t x2,y2;
	x2 = x1+w;
	y2 = y1+h;
	int rvalue = 0;
	if ( (px >= x1) && (px <= x2))
	{
		// ok, x constraint met
		if ( (py >= y1) && (py <= y2))
			rvalue = 1;
	}
	return rvalue;
}

void setupIO()
{
	RCC->AHBENR |= (1 << 18) + (1 << 17); // enable Ports A and B
	display_begin();
	pinMode(GPIOB,4,0);
	pinMode(GPIOB,5,0);
	pinMode(GPIOA,8,0);
	pinMode(GPIOA,11,0);
	enablePullUp(GPIOB,4);
	enablePullUp(GPIOB,5);
	enablePullUp(GPIOA,11);
	enablePullUp(GPIOA,8);
}


